  emits: ['refreshData', 'switchNav'],
  // 事件
  methods: {
    switchRefresh(index) {
      if (this.navIndex == index) {
        // 刷新
        this.$emit("refreshData")
      } else {
        // 切换
        this.$emit("switchNav", { name: this.navList[index].name })
        this.navIndex = index;
      }
    },
  }
}
